INSERT INTO InspectRecord (InspectPlan_Id, ExecuteStartTime)
VALUES (#planID#, '$time$')

	SELECT last_insert_id() as inspectrecordid;

INSERT INTO mediainfo (Type, Time, Address, HashValue)
VALUES (#type#, '$time$', #address#, #hash#)

	SELECT last_insert_id() as mediainfoid;

INSERT INTO ReleateMediaInfo (MediaInfo_Id, Type, ReleateRecordId,Remark)
